import React, { useState } from 'react'
import Header from '../../components/header/Header'
import Navbar from "../../components/navbar/Navbar"
import MailList from "../../components/mailList/MailList"
import Footer from "../../components/Footer/Footer"
import "./hotel.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faLocationDot, faCircleXmark, faCircleArrowLeft, faCircleArrowRight} from '@fortawesome/free-solid-svg-icons'
import useFetch from '../../hooks/useFetch'
import { useLocation } from 'react-router-dom'

const Hotel = () => {
  const location=useLocation();
  const id=location.pathname.split("/"[2]);
  console.log(location);
  const [slideNumber,setSlideNumber]=useState(0);
  const [open,setOpen]=useState(false);
  const {data, loading, error} = useFetch(`/hotels/${id}}`);
  const photos=[
    {
      src: "https://i2-prod.mirror.co.uk/incoming/article23060062.ece/ALTERNATES/s615b/0_Dolce-Hanoi-Golden-Lake-Hotel.jpg"
    },
    {
      src:"https://www.gyproc.in/images/gyproc-solutions-for-hotels-hospitality-sector.jpg"
    },
    {
      src:"https://r2imghtlak.mmtcdn.com/r2-mmt-htl-image/htl-imgs/201806011724173227-deae1014e76711e99f100242ac110002.jpg?&output-quality=75&downsize=910:612&crop=910:612;3,0&output-format=jpg"
    },
    {
      src:"https://hotelinteriordesigns.eu/wp-content/uploads/2017/04/10-Top-Hotel-Interior-Designs-All-Over-The-World-3.jpg"
    },
    {
      src:"https://hashtaglegend.com/storage/app/media/uploaded-files/aaa-lisboa-2.jpg"
    },
    {
      src:"https://cultureandheritage.org/wp-content/uploads/2021/10/abcd-32-1200x588.jpg"
    },
    {
      src:"https://thegrandhotelnuwaraeliya.com/wp-content/uploads/2018/11/banner1-3-1.jpg"
    },
    {
      src:"https://i.pinimg.com/originals/45/2e/fc/452efce25fc518dd522c352c035cd8c1.jpg"
    },
    {
      src:"https://www.luxurysafes.me/blog/wp-content/uploads/2020/05/Inside-ME-Dubai-By-Meli%C3%A1-Zaha-Hadid%E2%80%99s-Final-Boutique-Hotel-Project-18.jpg"
    },
  ]
  const handleOpen=(i)=>{
    setSlideNumber(i);
    setOpen(true)
  }
  const handleMove=(dir)=>{
    let newSlideNumber;
    if(dir==="l"){
      newSlideNumber=slideNumber===0?8:slideNumber-1;
    } else{
      newSlideNumber=slideNumber===8?0:slideNumber+1;
    }
    setSlideNumber(newSlideNumber)
  }
  return (
    <div>
      <Navbar/>
      <Header type="list" />
      {loading? ("Loading") : (<div className="hotelContainer">
        {open && <div className="slider">
        <FontAwesomeIcon icon={faCircleXmark} className='close' onClick={()=>setOpen(false)} />
        <FontAwesomeIcon icon={faCircleArrowLeft} className='arrow' onClick={()=>handleMove("l")} />
        <div className="slideWrapper">
          <img src={photos[slideNumber].src} alt="" className="sliderImg" />
        </div>
        <FontAwesomeIcon icon={faCircleArrowRight} className='arrow' onClick={()=>handleMove("r")}/>
        </div>}
        <div className="hotelWrapper">
          <button className="bookNow">Reserve or Book Now!</button>
          <h1 className="hotelTitle">{data.name}</h1>
          <div className="hotelAddress">
            <FontAwesomeIcon icon={faLocationDot}/>
            <span>{da}</span>
          </div>
          <span className="hotelDistance">Excellent location - 500 from center</span>
          <span className='hotelPriceHighlight' >Book a stay over $114 at this property and get a free airport taxi</span>
          <div className="hotelImages">
            {photos.map((photo,i)=>(
              <div className="hotelImgWrapper">
                <img src={photo.src} alt="" className="hotelImg" onClick={()=>handleOpen(i)} />
              </div>
            ))}
          </div>
          <div className="hotelDetails">
            <div className="hotelDetailsText">
              <h1 className='hotelTitle' >Get the celebrity treatment with world-class service at Taj Lakefront Bhopal</h1>
              <p className="hotelDesc">
              Set in Bhopal, 1.8 km from Museum of Man, Taj Lakefront Bhopal offers accommodation with a restaurant, free private parking, a seasonal outdoor swimming pool and a fitness centre. Featuring a bar, the 5-star hotel has air-conditioned rooms with free WiFi. The accommodation features a 24-hour front desk, room service and currency exchange for guests.

The hotel provides certain units that feature a balcony, and all rooms are equipped with a coffee machine. The units will provide guests with a minibar.

Taj Lakefront Bhopal offers a continental or buffet breakfast.
              </p>
            </div>
            <div className="hotelDetailsPrice">
              <h1>Perfect for a night stay</h1>
              <span>Located in real heart of Bhopal, this property has an excellent location score of 9.8</span>
              <h2><b>$940 </b>(9 Nights) </h2>
              <button>Book Now</button>
            </div>
          </div>
        </div>
        <MailList/>
        <Footer/>
      </div>)}
    </div>
  )
}

export default Hotel
